import React from 'react';
let titulo = "Mi primer sitio con React JS";
/* import parrafo from '../data/articulo'; */
let parrafo = require('../data/articulo');
let image = "https://placeimg.com/500/240/nature";

function Content() {
  return (
    <div className="row my-3">
      <div className="col">
        <div className="row">
          <div className="col">
            <h1>{titulo}</h1>
          </div>
        </div>

        <div className="row my-3">
          <div className="col-md-4">
            <img style={{ width: '100%' }} src={image} alt={'articulo'} />
            <a target="_blank" className="text-secondary" href={image}>
              Fuente: {image}
            </a>
          </div>

          <div className="col-md-8">
            <p>{parrafo}</p>
          </div>
        </div>

        <div className="row my-3">
          <div className="col">
            <p>{parrafo}</p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Content;
